bool gui_draw_text(struct gui_item* base, uint16_t *buffer, uint8_t x)
{
    struct gui_text_data* data = (struct gui_text_data*)base->raw_data;
    const uint8_t posX = base->sort_data.data.pos_x;
    const uint8_t posY = data->pos_y;
    const uint8_t scale = data->scale;
    const uint8_t x_absolute = (x - posX) / scale;
    const uint16_t color = data->color;
    const char letter = data->text[(x_absolute) / 6];
    if (letter == 0) {
        return false;
    }

    
    int bits = CharMap[letter - 32][(x_absolute) % 6];
    for (int yy = 0; yy < 8*scale; yy++) {
        if (bits >> (7 - yy/scale) & 1) {
            buffer[yy + posY] = color;
        }
    }
    return true;
}

struct gui_item* gui_make_text(uint8_t posX, uint8_t posY, uint8_t z_index, uint16_t color, const char* text, uint8_t scale)
{
    struct gui_item* this = gui_allocate_item();
    struct gui_text_data* data = (struct gui_text_data*)this->raw_data;
    this->sort_data.data.z_index = z_index;
    this->sort_data.data.pos_x = posX;
    this->type = 1;
    data->pos_y = posY;
    data->color = color;
    data->scale = scale;
    data->text = text;
    return this;
}
